import { Context } from './../../App';

const AddWorkLogModal = ({getData, setData}) => {

  const _context = useContext(Context);

  const _POST_WORKLOG = useCallback(() => {
    let category,date,id,reference,title,contents,user;
    category = 
    id = getData.length === 0 ? 20001 : getData[getData.length - 1].id + 1;

    $usePost(`${_context.databaseURL}/workLog/worklog.json`, {
      category: '일간보고',
      date: '2021-07-10',
      id: id,
      reference: [10002,10002,10001],
      title: '테스트 입니다.',
      contents: '지금은 테스트 중입니다.',
      user: 10002
    }, ({data}) => {
      console.log(data);
      setData((before) => {
        let _temp = [...before];
        _temp.push(data);
        return _temp;
      });
      console.log(data);
    });
  }, [_context.databaseURL, getData, setData]);

  return (
    <>
      {/* 제목: <input type="text" /><br />
      보고종류: <input type="text" /><br />
      사용자: <input type="text" /><br />
      참조: <input type="text" /><br />
      <button onClick={_POST_WORKLOG}>등록</button> */}

    </>
  )
}

export default AddWorkLogModal;
