import React, { useContext } from 'react';
import Styled from 'styled-components';
import { Context } from '../../App';

const CommonModalFrame = ({title, modal}) => {

  const _context = useContext(Context);

  const ModalClose = () => {
    _context.setModalOpen(false)
  }
  
  if (!_context.modalOpen) {
    return <></>;
  }

  return (
    <>
      <BlackBg onClick={ ModalClose }></BlackBg>
      <Pop>
        <Title bgColor={_context.mainColor}>
          { title }
          <Xbtn />
        </Title>
        { modal }
      </Pop>
    </>
  )
}

export default CommonModalFrame;

const BlackBg = Styled.div`
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background: #00000050;
  z-index: 5;
`;
const Pop = Styled.div`
  width: auto;
  height: auto;
  min-width: 300px;
  min-height: 300px;
  position: fixed;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  background: #fff;
  border-radius: 5px;
  overflow: hidden;
  z-index: 10;
`;
const Title = Styled.h2`
  font-size: 16px;
  padding: 10px;
  color: #fff;
  font-weight: 500;
  background: ${({bgColor}) => bgColor};
  display: flex;
  align-items: center;
  justify-content: space-between;
`;
const Xbtn = Styled.div`
  width: 24px;
  height: 24px;
  position: relative;
  cursor: pointer;

  &::before {
    content: '';
    width: 100%;
    height: 2px;
    background-color: #fff;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(45deg);
  }
  &::after {
    content: '';
    width: 100%;
    height: 2px;
    background-color: #fff;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(-45deg);
  }

  &:hover::before, &:hover::after {
    background-color: #aaa;
  }
  &:active::before, &:active::after {
    background-color: #888;
  }
`;
