const $useGet = (url = '', callback) => {
  let result = 'Fail';
  let data = [];
  let loading = true;
  
    fetch(url).then(res => {
      if (res.status !== 200) {
        console.error(`${url} Server Error`);
        return;
      }
      return res.json();
    }).then(resData => {
      result = 'Success';
      data = resData;
      loading = false;
      callback({result: result, data: data, loading: loading});

    }).catch((error) => {
      result = 'Fail';
      data = null;
      loading = true;
      callback({result: result, data: data, loading: loading});

    });
}

export default $useGet;
