import React, { useCallback, useContext, useEffect, useState } from 'react';
import Styled from 'styled-components';
import { ColorList } from './../Contents';
import { Context } from './../../App';
import Log from './WorkLog/Log';

const WorkLog = () => {

  const databaseURL = useContext(Context);
  console.log(databaseURL);
  const [workLog, setWorkLog] = useState([
    {id: 10001, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002, 10003, 10004]},
    {id: 10002, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002, 10003]},
    {id: 10003, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002]},
    {id: 10004, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002]},
    {id: 10005, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002, 10003]},
    {id: 10006, date: '2021-07-01', title: '제목입니다. 제목입니다. 제목입니다. 제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: []},
    {id: 10007, date: '2021-07-01', title: '제목입니다. 제목입니다. 제목입니다. 제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002, 10003]},
  ]);

  const _context = useContext(Context);
  const [loading, setLoading] = useState('ture');

  const _GET_USERLIST = useCallback(() => {
    fetch(`${databaseURL}/userList.json`).then(res => {
      if (res.status !== 200) {
        console.error('서버에러!!');
        return;
      }
      return res.json();
    }).then(data => {
      console.log(data);
      setWorkLog(data ?? []);
      setTimeout(() => setLoading(false), 500);
    });
  }, [databaseURL, setWorkLog]);





  useEffect(() => {
    console.log('Work Log 리스트가 변경되었습니다.');
  }, [workLog]);

  const addBoardBtnClick = useCallback(() => {
    _context.setModalOpen(true);
  }, [_context]);
  return (
    <>
      <Title>Work Log Board <AddBtn onClick={ addBoardBtnClick }/></Title>
      <LogListHead>
        <div>ID</div>
        <div>Date</div>
        <div>Title</div>
        <div>Category</div>
        <div>User</div>
        <div>Reference</div>
      </LogListHead>
      <LogListBody>
        {workLog.map(data => (
          <Log logData={ data } key={ data.id } />
        ))}
      </LogListBody>
    </>
  )
}

export default WorkLog;

const Title = Styled.h2`
  font-size: 16px;
  font-weight: 500;
  font-family: 'Baloo Tammudu 2', cursive;
  border-bottom: 1px solid #eee;
  white-space: nowrap;
`;
const AddBtn = Styled.button`
  width: 30px;
  height: 30px;
  float: right;
  position: relative;
  border: none;
  border-radius: 4px;
  background-color: #338f6c;
  ::before {
    content: '';
    width: 60%;
    height: 2px;
    background-color: #fff;
    display: block;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
  }
  ::after {
    content: '';
    width: 60%;
    height: 2px;
    background-color: #fff;
    display: block;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(90deg);
  }
  :hover {background-color: #359773;}
  :active {background-color: #2e8162;}
`;
const LogListHead = Styled.section`
  display: flex;
  align-items: center;
  justify-content: flex-start;
  border-bottom: 1px solid #eee;
  div:nth-of-type(1) {width: 8%;}
  div:nth-of-type(2) {width: 10%;}
  div:nth-of-type(3) {width: 32%;}
  div:nth-of-type(4) {width: 15%;}
  div:nth-of-type(5) {width: 15%;}
  div:nth-of-type(6) {width: 20%;}
  div {
    height: 100%;
    padding: 20px 0 10px;
    font-size: 12px;
    color: #555;
    letter-spacing: 1px;
  }
`;
const LogListBody = Styled.ul`
  width: 100%;
  height: calc(100% - 38px - 48px);
  overflow: auto;
`;
