import { useState, useEffect } from "react";

const $useGet = (url = '', callback) => {
  const [result, setResult] = useState();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  const async = (url, callback) => {
    fetch(url).then(res => {
      if (res.status !== 200) {
        console.error(`${url} Server Error`);
        return;
      }
      return res.json();
    }).then(data => {
      setResult('Success');
      setData(data);
      setLoading(false);
      callback(
        {result, data, loading}

      );

    }).catch((error) => {
      setResult('Fail');
      setData(null);
      setLoading(true);

    });
  }

  useEffect(() => {
    async();
  }, []);

  return [result, data, loading];
}

export default $useGet;
