export const $useGet = (url = '', callback) => {
  let result = 'Fail';
  let data = [];
  
    fetch(url).then(res => {
      if (res.status !== 200) {
        console.error(`${url} Server Error`);
        return;
      }
      return res.json();
    }).then(resData => {
      result = 'Success';
      data = resData ?? [];
      callback({result: result, data: data});
    }).catch((error) => {
      result = 'Fail';
      data = null;
      callback({result: result, data: data});
    });
}
