import React, { useContext } from 'react';
import Styled from 'styled-components';
import { Context } from './../App';
import { addWorkLogModal } from './modal/';

const Modal = (props) => {

  const _context = useContext(Context);

  const ModalClose = () => {
    _context.setModalOpen(false)
  }

  if (!props.modalOpen) {
    return <></>;
  }
  return (
    <>
      <BlackBg onClick={ ModalClose }></BlackBg>
      <Pop>
        <Title></Title>
        모달 예시
      </Pop>
    </>
  )
}

const BlackBg = Styled.div`
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background: #00000050;
  z-index: 5;
`;
const Pop = Styled.div`
  width: auto;
  height: auto;
  min-width: 300px;
  min-height: 300px;
  position: fixed;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  background: #fff;
  z-index: 10;
`;
const Title = Styled.h2`
  &::before {
    content: ${(title = '제목없음') => title}
  }
`;


export default Modal;
