import React, { useContext, useEffect, useState } from 'react';
import Styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-solid-svg-icons';
import { ColorList } from './../Contents';
import { Context } from './../../App';

const WorkLog = () => {

    const _context = useContext(Context);

    const [workLog, setWorkLog] = useState([
        {id: 10001, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002, 10003, 10004]},
        {id: 10002, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002, 10003]},
        {id: 10003, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002]},
        {id: 10004, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002]},
        {id: 10005, date: '2021-07-01', title: '제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002, 10003]},
        {id: 10006, date: '2021-07-01', title: '제목입니다. 제목입니다. 제목입니다. 제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: []},
        {id: 10007, date: '2021-07-01', title: '제목입니다. 제목입니다. 제목입니다. 제목입니다.', contents: '내용입니다.', category: '주간보고', user: 'JEON', reference: [10002, 10003]},
    ]);

    useEffect(() => {
        console.log('Work Log 리스트가 변경되었습니다.');
    }, [workLog]);

    const addBoardBtnClick = () => {
        _context.setModalOpen(true);
    }
    return (
        <>
            <Title>Work Log Board <AddBtn onClick={ addBoardBtnClick }/></Title>
            <LogListHead>
                <div>ID</div>
                <div>Date</div>
                <div>Title</div>
                <div>Category</div>
                <div>User</div>
                <div>Reference</div>
            </LogListHead>
            <LogListBody>
                {workLog.map((data, idx) => (
                    <Log logData={ data } key={ idx } />
                ))}
            </LogListBody>
        </>
    )
}

const Log = ({logData}) => {

    const color = useContext(ColorList);

    return (
        <LogLi>
            <div>{ logData.id }</div>
            <div>{ logData.date }</div>
            <div>{ logData.title }</div>
            <div>{ logData.category }</div>
            <div>{ logData.user }</div>
            <div>{ logData.reference.map((data, idx) => {
                let randomNum = parseInt(Math.random() * 10);

                return (
                    <FontAwesomeIcon 
                        key={ idx }
                        icon={ faUser }
                        style={ 
                            {
                                fontSize: '18px',
                                borderRadius: '50%',
                                width: '26px',
                                height: '26px',
                                padding: '6px',
                                color: '#fff',
                                marginRight: '-8px',
                                border: '1px solid #fff',
                                background: color[randomNum]
                            } 
                        }
                        title={ data.toString() }
                    />
                )
            }) }</div>
        </LogLi>
    )
}

export default WorkLog;

const Title = Styled.h2`
    font-size: 16px;
    font-weight: 500;
    font-family: 'Baloo Tammudu 2', cursive;
    border-bottom: 1px solid #eee;
    white-space: nowrap;
`;
const AddBtn = Styled.button`
    width: 30px;
    height: 30px;
    float: right;
    position: relative;
    border: none;
    border-radius: 4px;
    background-color: #338f6c;
    ::before {
        content: '';
        width: 60%;
        height: 2px;
        background-color: #fff;
        display: block;
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
    }
    ::after {
        content: '';
        width: 60%;
        height: 2px;
        background-color: #fff;
        display: block;
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%) rotate(90deg);
    }
    :hover {background-color: #359773;}
    :active {background-color: #2e8162;}
`;
const LogListHead = Styled.section`
    display: flex;
    align-items: center;
    justify-content: flex-start;
    border-bottom: 1px solid #eee;
    div:nth-of-type(1) {width: 8%;}
    div:nth-of-type(2) {width: 10%;}
    div:nth-of-type(3) {width: 32%;}
    div:nth-of-type(4) {width: 15%;}
    div:nth-of-type(5) {width: 15%;}
    div:nth-of-type(6) {width: 20%;}
    div {
        height: 100%;
        padding: 20px 0 10px;
        font-size: 12px;
        color: #555;
        letter-spacing: 1px;
    }
`;
const LogListBody = Styled.ul`
    width: 100%;
    height: calc(100% - 38px - 48px);
    overflow: auto;
`;
const LogLi = Styled.li`
    display: flex;
    align-items: center;
    div:nth-of-type(1) {width: 8%;}
    div:nth-of-type(2) {width: 10%;}
    div:nth-of-type(3) {width: 32%;}
    div:nth-of-type(4) {width: 15%;}
    div:nth-of-type(5) {width: 15%;}
    div:nth-of-type(6) {width: 20%; overflow: auto;}
    div {
        height: 100%;
        padding: 14px 0;
        font-size: 13px;
        color: #222;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        padding-right: 10px;
    }
`;
