import React, { useState, useCallback } from 'react';
import { SideMenu, Contents, Modal } from './component';
import Styled from 'styled-components';
import {
  faHome,
  faUser,
  faClipboard,
  faBell,
  faCogs
} from '@fortawesome/free-solid-svg-icons';

export const Context = React.createContext({});

const Component = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [mainColor, setMainColor] = useState('#338f6c');
  const [programName, setProgramName] = useState('Work Station');
  const [menus, setMenus] = useState({
    main: [
      {id: 0, name: 'Home', icon: faHome, link: '/'},
      {id: 1, name: 'Work Log', icon: faClipboard, link: '/WorkLog'},
      {id: 2, name: 'User List', icon: faUser, link: '/UserList'}
    ],
    sub: [
      {id: 3, name: 'Support', icon: faBell, link: '/Support'},
      {id: 4, name: 'Setting', icon: faCogs, link: '/Setting'}
    ]
  });
  // eslint-disable-next-line no-extend-native
  Date.prototype.getString = useCallback(function () {
    let Y = String(this.getFullYear());
    let M = String(this.getMonth()).length === 1 ? '0' + String(this.getMonth() + 1) : String(this.getMonth() + 1);
    let D = String(this.getDate()).length === 1 ? '0' + String(this.getDate()) : String(this.getDate());
    let h = String(this.getHours()).length === 1 ? '0' + String(this.getHours()) : String(this.getHours());
    let m = String(this.getMinutes()).length === 1 ? '0' + String(this.getMinutes()) : String(this.getMinutes());
    let s = String(this.getSeconds()).length === 1 ? '0' + String(this.getSeconds()) : String(this.getSeconds());
    return `${Y}-${M}-${D} ${h}:${m}:${s}`;
  }, []);

  return (
    <App mainColor={mainColor}>
      <Context.Provider
        value={{
          menus, setMenus,
          mainColor, setMainColor,
          modalOpen, setModalOpen,
          programName, setProgramName
        }}
      >
        <title id="title">{programName}</title>
        <Modal modalOpen={modalOpen}/>
        <SideMenu programName={programName} />
        <Contents />
      </Context.Provider>
    </App>
  );

}

export default Component;

// Style
const App = Styled.div`
  width: 100%;
  height: 100%;
  padding: 10px;
  background: ${(props) => props.mainColor};
  border-radius: 26px;
`;
