export const $useGet = (url = '', callback) => {
  let result = 'Fail';
  let data = [];
  
  fetch(url).then(res => {
    if (res.status !== 200) {
      console.error(`${url} Server Error`);
      return;
    }
    return res.json();
  }).then(resData => {
    result = 'Success';
    data = resData ?? [];
    callback({result: result, data: data});
  }).catch((error) => {
    result = 'Fail';
    data = null;
    callback({result: result, data: data});
  }).then(() => {
    callback({result: result, data: data});
    console.log('Complete');
  });
}

export const $usePost = (url = '', newData, callback) => {
  let result = 'Fail';

  fetch(url, {
    method: 'POST',
    body: JSON.stringify(newData)
  }).then(res => {
    if (res.status !== 200) {
      console.error(`${url} Server Error`);
      return;
    }
  }).then((resData) => {
    result = 'Success';
    callback({result: result});
  }).catch((error) => {
    result = 'Fail';
    callback({result: result})
  })
  
}
