import React, { useCallback, useContext } from 'react';
import Styled from 'styled-components';
import { $usePost } from './../../hook/useFirebase';
import { Context } from './../../App';

const AddWorkLogModal = ({setFn}) => {

  const _context = useContext(Context);

  const _POST_WORKLOG = useCallback(() => {
    $usePost(`${_context.databaseURL}/workLog/worklog.json`, {
      category: '일간보고',
      date: '2021-07-10',
      id: '20002',
      reference: [10002,10002,10001],
      title: '테스트 입니다.',
      contents: '지금은 테스트 중입니다.',
      user: 10002
    }, ({data}) => {
      console.log(data);
      setFn(data);
      console.log(data);
    }, setFn);
  }, [_context.databaseURL, setFn]);

  return (
    <>
      제목: <input type="text" /><br />
      보고종류: <input type="text" /><br />
      사용자: <input type="text" /><br />
      참조: <input type="text" /><br />
      <button onClick={_POST_WORKLOG}>등록</button>

    </>
  )
}

export default AddWorkLogModal;
